// src/App.js
import React, { useState, Component } from 'react';
import RecipeList from './RecipeList';
import SearchBar from './SearchBar';
import './App.css';
import SearchBox from '../components/SearchBox';

const App = () => {
  const recipes = [
    {
      id: 1,
      name: 'Spaghetti Carbonara',
      ingredients: ['Spaghetti', 'Eggs', 'Parmesan Cheese', 'Pancetta', 'Pepper'],
      instructions: 'Cook spaghetti. In a separate pan, cook pancetta. Beat eggs and cheese together. Combine spaghetti with pancetta, then mix with egg and cheese. Serve with pepper.',
      category: 'Italian',
      image: 'https://example.com/spaghetti.jpg'
    },
    {
      id: 2,
      name: 'Chicken Curry',
      ingredients: ['Chicken', 'Curry Powder', 'Coconut Milk', 'Onion', 'Garlic'],
      instructions: 'Cook onions and garlic, add chicken and curry powder. Pour in coconut milk and simmer. Serve with rice.',
      category: 'Indian',
      image: 'https://example.com/chicken-curry.jpg'
    },
    {
      id: 3,
      name: 'Vegetarian Tacos',
      ingredients: ['Tortillas', 'Beans', 'Lettuce', 'Cheese', 'Salsa'],
      instructions: 'Warm tortillas, fill with beans, lettuce, cheese, and salsa. Serve with a squeeze of lime.',
      category: 'Mexican',
      image: 'https://example.com/tacos.jpg'
    }
  ];

  const [search, setSearch] = useState('');
  const [filteredRecipes, setFilteredRecipes] = useState(recipes);

  const handleSearchChange = (event) => {
    setSearch(event.target.value);
    const searchQuery = event.target.value.toLowerCase();
    const filtered = recipes.filter(recipe =>
      recipe.name.toLowerCase().includes(searchQuery) ||
      recipe.category.toLowerCase().includes(searchQuery)
    );
    setFilteredRecipes(filtered);
  };

  return (
    <div className="App">
      <h1>Simple Recipe Finder</h1>
      <SearchBox search={search} onSearchChange={handleSearchChange} />
      <RecipeList recipes={filteredRecipes} />
    </div>
  );
};

export default App;
